SELECT employee_in.name,emplyin_breakrules.empInBreakContent,emplyin_breakrules.empInBreakTime,emplyin_breakrules.empInMinusNum FROM employee_in,department,emplyin_breakrules where department.departmentid=#{breakrulesbumen} and department.departmentId=employee_in.departmentId and employee_in.employeeId=emplyin_breakrules.empInEmployeeId
and emplyin_breakrules.empInBreakTime >= #{fstarttime}
and emplyin_breakrules.empInBreakTime <= #{fendtime}
AND emplyin_breakrules.empInBreakTime LIKE CONCAT(Year(CurDate()),'%') order by emplyin_breakrules.empInBreakTime DESC

		SELECT count(emplyin_breakrules.empInBreakId) FROM
		employee_in,department,emplyin_breakrules where
