mapper.select:
	SELECT COUNT(*) 
	FROM employee_out,haulemployeeout,haulinfo 

  WHERE employee_out.employeeId = haulemployeeout.employeeId AND haulemployeeout.bigId = haulinfo.bigId 

	AND employee_out.name LIKE '%${employeeOutName}%' AND employee_out.sex LIKE '%${employeeOutSex}%' AND employee_out.idCode LIKE '%${employeeOutIdCard}%' AND haulemployeeout.empType like '%${employeeType}%'
	
		and (CASE (SELECT	id FROM blacklist WHERE blacklist.blackIdcard = haulemployeeout.empoutIdcard AND temporaryInStatus =1 AND employeeStatus="1") IS NULL WHEN TRUE THEN "否" ELSE "是" END ) = #{blackListInfo}
	
	
	
	
	
		and haulemployeeout.unitid = #{unitId}
	
	
	
		and haulemployeeout.bigid = #{bigId}
	
	
	
		and employee_out.address like "%${address}%"
	
	
	
		and TIMESTAMPDIFF(YEAR, employee_out.birthday, CURDATE()) >= #{age_left}
	
	
	
		and TIMESTAMPDIFF(YEAR, employee_out.birthday, CURDATE()) <= #{age_right}
	
	
	
	
	
	
		and haulemployeeout.bigId IN (#{regular_train},#{longterm_train})
	
	
	
		and haulemployeeout.bigId NOT IN (#{regular_train},#{longterm_train})
	
	
	
	
		and haulinfo.bigStatus != #{bigStatus}
	
	
	
		and 
		
	
	
		and

SELECT employee_out.`name`,employee_out.idCode idCard,employee_out.sex,employee_out.address,employee_out.birthday,employee_out.headaddress photo, haulemployeeout.*,haulinfo.bigBeginDate,haulinfo.bigCreateDate,haulinfo.bigdescription,haulinfo.bigEndDate,haulinfo.bigName,haulinfo.bigStatus, (SELECT unit.`name` FROM unit WHERE unitId = haulemployeeout.unitId) departmentName, CASE (SELECT SUM(breakrules.minusNum) FROM breakrules WHERE employeeId = haulemployeeout.employeeId AND breakTime LIKE CONCAT(YEAR(CURDATE()),'%')) IS NULL WHEN TRUE THEN 0 ELSE (SELECT SUM(breakrules.minusNum) FROM breakrules WHERE employeeId = haulemployeeout.employeeId AND breakTime LIKE CONCAT(YEAR(CURDATE()),'%')) END minusNum, CASE (SELECT SUM(breakrules.minusNum) FROM breakrules WHERE employeeId = haulemployeeout.employeeId ) IS NULL WHEN TRUE THEN 0 ELSE (SELECT SUM(breakrules.minusNum) FROM breakrules WHERE employeeId = haulemployeeout.employeeId )END minusNumSum,
IFNULL((SELECT SUM(breakrules.minusNum) FROM breakrules WHERE breakrules.employeeId = employee_out.employeeId
and breakTime >= #{fstarttime}
and breakTime <= #{fendtime}
and breakrules.breakTime LIKE '${current_Year}%'
AND breakrules.breakTime between #{history_Year} AND NOW()
),0) AS 'sumBreakScore',
CASE (SELECT id FROM blacklist WHERE blacklist.blackIdcard = haulemployeeout.empoutIdcard AND temporaryInStatus =1 AND employeeStatus="1") IS NULL WHEN TRUE THEN "否" ELSE "是" END isblackList, CASE (SELECT id FROM blacklist WHERE blacklist.blackIdcard = haulemployeeout.empoutIdcard AND employeeStatus="1") IS NULL WHEN TRUE THEN "否" ELSE "是" END isInBlackList
FROM employee_out,haulemployeeout,haulinfo
WHERE employee_out.employeeId = haulemployeeout.employeeId AND haulemployeeout.bigId = haulinfo.bigId
AND employee_out.name LIKE '%${employeeOutName}%' AND employee_out.sex LIKE '%${employeeOutSex}%' AND employee_out.idCode LIKE '%${employeeOutIdCard}%' AND haulemployeeout.empType like '%${employeeType}%'
and (CASE (SELECT id FROM blacklist WHERE blacklist.blackIdcard = haulemployeeout.empoutIdcard AND temporaryInStatus =1 AND employeeStatus="1") IS NULL WHEN TRUE THEN "否" ELSE "是" END ) = #{blackListInfo}
and haulemployeeout.unitid = #{unitId}
and haulemployeeout.bigid = #{bigId}
and employee_out.address like "%${address}%"
and TIMESTAMPDIFF(YEAR, employee_out.birthday, CURDATE()) >= #{age_left}
and TIMESTAMPDIFF(YEAR, employee_out.birthday, CURDATE()) <= #{age_right}
and haulemployeeout.bigId IN (#{regular_train},#{longterm_train})
and haulemployeeout.bigId NOT IN (#{regular_train},#{longterm_train})
and haulinfo.bigStatus != #{bigStatus}
and
and
ORDER BY haulinfo.bigCreateDate DESC , sumBreakScore DESC

mapper.sql:
IFNULL((SELECT SUM(breakrules.minusNum) FROM breakrules
WHERE
breakrules.employeeId = employee_out.employeeId

	and breakTime >= #{fstarttime}


	and breakTime <= #{fendtime}



	and breakrules.breakTime LIKE  '${current_Year}%'



	AND breakrules.breakTime between #{history_Year} AND NOW()
 
),0) >= #{minusNumLeft}

IFNULL((SELECT SUM(breakrules.minusNum) FROM breakrules
WHERE
breakrules.employeeId = employee_out.employeeId

	and breakTime >= #{fstarttime}


	and breakTime <= #{fendtime}



	and breakrules.breakTime LIKE  '${current_Year}%'



	AND breakrules.breakTime between #{history_Year} AND NOW()
 
),0) <= #{minusNumRight}
